import logo from './logo.svg';
import './App.css';
import React,{useState} from 'react';





 function App() {
  const [row,setRow]=useState(0);
  const [column,setColumn]=useState(0);
  let rowContent =[] ;
  let columnContent=[];
  let stateHomes=[];
  const clearColor='white';
  const polluterColor='aqua';



















  const set_dimension=()=>{
    const enteredRow=document.getElementById('row-App').value;
    const enteredColumn=document.getElementById('column-App').value;
    setRow(enteredRow);
    setColumn(enteredColumn);
  }

  const change_state_home=(event)=>{
    if(stateHomes[event.target.id]==true){

      console.log(event.target.id)
      document.getElementById(event.target.id).className='polluter';
      console.log(document.getElementById(event.target.id).className)
      console.log('clear change polluter')
      stateHomes[event.target.id]=false;
    }else{
      stateHomes[event.target.id]=true;
      document.getElementById(event.target.id).className='home-App';
      console.log('polluter change clear')
    }



  }

  let [repeet,setRepeet]=useState(false);
  const set_howWork=()=>{
    setRepeet(!repeet);
  }




  let counter=0;
  for(let i=0;i<row;i++){
    columnContent[i]=[];
    if((i%2)==0){
      for(let j=0;j<column;j++){
        stateHomes[counter]=true;
        columnContent[i][j]=(<div style={{width:(100/column)+'%'}} className='homes-App' id={counter++}  onClick={change_state_home}   >({i},{j})</div>)
    }

    }else{
      for(let j=column-1;j>=0;j--){
        stateHomes[counter]=true;
        columnContent[i][j]=(<div style={{width:(100/column)+'%'}} className='homes-App' id={counter++}  onClick={change_state_home}   >({i},{j})</div>)
    }


    }
    rowContent.push(<div style={{height:(100/row)+'%',minWidth:'25px'}} className='rows-App'>{columnContent[i]}</div>); 

}

let text='';

const show_time=()=>{}
let moveRight=true;
const cleaning =(i,text)=>{

  stateHomes[i]=true;
  document.getElementById(i.toString()).className='home-App';

  document.getElementById(i.toString()).innerHTML=text;

  setTimeout(move_robot.bind(this,i),2000)





}
const scaning=(i,text)=>{

  if(stateHomes[i]==false){
    console.log('clean here:'+i)
    setTimeout(cleaning.bind(this,i),2000)


  }else{
    document.getElementById(i.toString()).innerHTML=null;
    console.log('move on'+ (i+1) +'home')
    setTimeout(move_robot.bind(this,i),2000)


  }

}

const move_robot=(i)=>{
  console.log(repeet);
  text=document.getElementById(i.toString()).innerHTML;




  if(repeet){
    if(moveRight){
      i+=1;
      if(i<stateHomes.length){
        console.log('scaning here:'+i)
        document.getElementById((i).toString()).innerHTML='<div></div>';
        setTimeout(scaning.bind(this,i),2000)
        }else{
          moveRight=false;
          document.getElementById((i-1).toString()).innerHTML=null;
          i-=2;
          document.getElementById((i).toString()).innerHTML='<div></div>';
          setTimeout(scaning.bind(this,i),2000)
        }

    }else{
      i-=1;
      if(i>=0){
        console.log('scaning here:'+i)
        document.getElementById((i).toString()).innerHTML='<div></div>';
        setTimeout(scaning.bind(this,i),2000)
        }else{
          moveRight=true;
          setTimeout(scaning.bind(this,++i),2000)

        }


    }

  }else{
    i+=1;
    if(i<stateHomes.length){
      console.log('scaning here:'+i)
      setTimeout(scaning.bind(this,i),2000)
      document.getElementById((i).toString()).innerHTML='<div></div>';
      }else{
        setTimeout(()=>{console.log('evry there clean:)')},1000)

      }

  }




}
async function on_robot(){

    let i=-1;
    console.log('move on'+ (i+1) +'home')
     setTimeout(move_robot.bind(this,i),2000)








}








  return (
    <div className="App">


      <div className="part2-App">
        <div className="carpet"></div>
        <div className='content-App'>
          {rowContent}
        </div>
      </div>

      <div className="part1-App">
          <div className="enteredInfo-App">
            <div className="Rows-app"><label htmlFor="">سطر</label><input className='enterData' id='row-App' type="text" /></div>
            <div className="coulom-App" ><label htmlFor="">ستون</label><input className='enterData' id='column-App' type="text" /></div>
            <div className="howWork" ><label htmlFor="">جارو برقی بدون وقفه کار کند</label><input type="checkbox" className='enterData' id='howWork' onClick={set_howWork} /></div>
            <div className="setDimension-App" ><button onClick={set_dimension}>اعمال</button></div>
        </div>
        <div className="controlPanel-App">
          <div className='part1'>
            <button onClick={on_robot}>شروع عملیات</button>
          </div>
          <div className="part2">
            <div className="processoer">

            </div>
            <div className="sensor">

            </div>
            <div className="performer">

            </div>
          </div>
        </div>
      </div>





    </div>
  );
}

export default App;
