  const [row,setRow]=useState(0);
  const [column,setColumn]=useState(0);
  let rowContent =[] ;
  let columnContent=[];
  let content=[];
  rowContent=[];





    for(let i=0;i<row;i++){

     for(let j=0;j<column;j++){



        columnContent[j]=(<div style={{width:(100/column)+'%',minWidth:'50px'}} ></div>)
     }

     rowContent[i]=(<div style={{height:(100/row)+'%',minHeight:'50px'}}>{columnContent}</div>); 

    }










  const set_dimension=()=>{
    const enteredRow=document.getElementById('row-App').value;
    const enteredColumn=document.getElementById('column-App').value;
    setRow(enteredRow);
    setColumn(enteredColumn);

  }










  return (
    <div className="App">

      <div className="part1-App">
        <div className="enteredInfo-App">
          <div className="Rows-app"><label htmlFor="">سطر</label><input id='row-App' type="text" /></div>
          <div className="coulom-App" ><label htmlFor="">ستون</label><input id='column-App' type="text" /></div>
        <div className="setDimension-App" ><button onClick={set_dimension}>اعمال</button></div>
      </div>

      </div>
      <div className="part2-App">


        <div className='content-App'>

        </div>
      </div>
      <div className="part3-App">

      </div>

    </div>
  );
}

export default App;
