import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';




function App() {
    const [row, setRow] = useState(0);
    const [column, setColumn] = useState(0);
    let rowContent = [];
    let columnContent = [];
    let stateHomes = [];
    const clearColor = 'white';
    const polluterColor = 'aqua';



















    const set_dimension = () => {
        const enteredRow = document.getElementById('row-App').value;
        const enteredColumn = document.getElementById('column-App').value;
        setRow(enteredRow);
        setColumn(enteredColumn);
    }

    const change_state_home = (event) => {


        if (stateHomes[event.target.id] == true) {
            stateHomes[event.target.id] = false;
            event.target.style.backgroundColor = polluterColor;
            console.log('clear change polluter')
        } else {
            stateHomes[event.target.id] = true;
            event.target.style.backgroundColor = clearColor;
            console.log('polluter change clear')
        }



    }

    const send_location = (event) => {

    }




    let counter = 0;
    for (let i = 0; i < row; i++) {
        columnContent[i] = [];


        for (let j = 0; j < column; j++) {
            stateHomes[counter] = true;
            columnContent[i][j] = ( < div style = {
                    { width: (100 / column) + '%', backgroundColor: clearColor } }
                className = 'homes-App'
                id = { counter++ }
                onClick = { change_state_home } > < /div>)

            }

            rowContent.push( < div style = {
                    { height: (100 / row) + '%', minWidth: '25px' } }
                className = 'rows-App' > { columnContent[i] } < /div>); 



            }



            const show_time = () => {}
            const on_robot = () => {
                for (let i = 0; i < stateHomes.length; i++) {
                    setTimeout('alert("salam")', 2000)
                    document.getElementById(i.toString()).innerHTML = < div > < /div>;
                    console.log(i)

                    //document.getElementById(i.toString()).innerHTML=null;

                }


            }








            return ( <
                div className = "App" >


                <
                div className = "part2-App" >
                <
                div className = "rowMark-App" > < /div> <
                div className = "columnMark-App" > < /div> <
                div className = 'content-App' > { rowContent } <
                /div> <
                /div>

                <
                div className = "part1-App" >
                <
                div className = "enteredInfo-App" >
                <
                div className = "Rows-app" > < label htmlFor = "" > سطر < /label><input id='row-App' type="text" / > < /div> <
                div className = "coulom-App" > < label htmlFor = "" > ستون < /label><input id='column-App' type="text" / > < /div> <
                div className = "setDimension-App" > < button onClick = { set_dimension } > اعمال < /button></div >
                <
                /div> <
                div className = "controlPanel-App" >
                <
                div className = 'part1' >
                <
                button onClick = { on_robot } > شروع عملیات < /button> <
                /div> <
                div className = "part2" > < /div> <
                /div> <
                /div>



                <
                /div>
            );
        }

        export default App;
