import logo from './logo.svg';
import './App.css';
import React,{useState} from 'react';
import Home from './Components/Home'




 function App() {
  const [row,setRow]=useState(0);
  const [column,setColumn]=useState(0);
  let rowContent =[] ;
  let columnContent=[];




  const clearColor='white';
  const polluterColor='aqua';
  const change_state_home=(event)=>{
      console.log(event.target.id)

      if(event.target.clear){
        event.target.clear=false;
        event.target.style.backgroundColor=polluterColor;
        console.log('clear change polluter')
      }else{
        event.target.clear=true;
        event.target.style.backgroundColor=clearColor;
        console.log('polluter change clear')
      }
    }















  const set_dimension=()=>{
    const enteredRow=document.getElementById('row-App').value;
    const enteredColumn=document.getElementById('column-App').value;
    setRow(enteredRow);
    setColumn(enteredColumn);
  }
  const create_row=()=>{
    for(let i=0;i<column;i++){
      columnContent[i]=<div className='Home' style={{width:(100/column)+'%',backgroundColor:clearColor}} id={i}  ></div>
    }

  }





  for(let i=0;i<row;i++){
    create_row();

    rowContent[i]=(<div style={{height:(100/row)+'%',minWidth:'25px'}} className='rows-App' id={i} onClick={change_state_home}>{columnContent}</div>); 
   }

   for(let i=0;i<row;i++){

   }










  return (
    <div className="App">


      <div className="part2-App">
        <div className="rowMark-App"></div>
        <div className="columnMark-App"></div>
        <div className='content-App'>
          {rowContent}
        </div>
      </div>

      <div className="part1-App">
        <div className="enteredInfo-App">
          <div className="Rows-app"><label htmlFor="">سطر</label><input id='row-App' type="text" /></div>
          <div className="coulom-App" ><label htmlFor="">ستون</label><input id='column-App' type="text" /></div>
        <div className="setDimension-App" ><button onClick={set_dimension}>اعمال</button></div>
      </div>
      </div>

      <div className="part3-App">

      </div>

    </div>
  );
}

export default App;
